package spring.project.helloworld.entity;public class Product {    public int id;    public String name;    public int stock;    public Product(int id, String name, int stock) {        this.id = id;        this.name = name;        this.stock = stock;    }    @Override    public String toString() {        return "Product{" +                "id=" + id +                ", name='" + name + '\'' +                ", stock=" + stock +                '}';    }}